using System.Collections;using System.Collections.Generic;using UnityEngine;public class Orbit : MonoBehaviour{	public const float CC = 1500.0f;	public float GM = 50000.0f;	public int type = 1;	public Vector3 v;	public bool bGeneral = false;	public GameObject orbitObj;		public Texture2D uitextures;    // Update is called once per frame    void Update()    {		float dtime = 0.01f;		Vector3 parentposition=Vector3.zero;		if(orbitObj)			parentposition = orbitObj.transform.position;		Vector3 position = transform.position-parentposition;		{// Newtown			Vector3 op = position.normalized;						float r = position.magnitude;			if (r < 0.75f)				return;						float f = -GM / (r * r) * 0.1f;			if(bGeneral)			{				float vr = Vector3.Dot(v, op);				float vt = Vector3.Dot(v, new Vector3(-op.z, op.y, op.x));				float nn = -(3.0f * vr * vr / CC - vt * vt / CC - 1.0f);				f *= nn;			}			v += position.normalized * (f * dtime);			position += v * dtime;		}		transform.position = position+parentposition;		    }}